import {useEffect, useState} from 'react'

const UserForm = (props) => {

  const [formValid, setFormValid] = useState(false)

  const [userName, setUserName] = useState("")
  const [nameValid, setNameValid] = useState({})
  const [userAge, setUserAge] = useState(0)
  const [userPhone, setUserPhone] = useState()
  const [userEmail, setUserEmail] = useState("")




  useEffect(()=>{
    if(userName.length === 0){
      setNameValid({valid:false, msg: "Bitte geben Sie Ihren Namen ein!"})
    }else if(userName.length < 3 ){
      setNameValid ({valid:false, msg: "Der Name ist zu kurz"})
    }else{
      setNameValid ({valid: true, msg:""})
    }
  }, [userName])



   useEffect(()=>{
    if(nameValid && nameValid.valid === true){
      setFormValid(true)
    }else{
      setFormValid(false)
    }
  }, [nameValid]) 

  return (
    <div id="UserForm" className="step">
      <h1>
        Ihre Daten
      </h1>
        <div className="input-wrapper">
          <div className="top">
            <label htmlFor="inputName">Name</label>
            <input type="text"
                   value={userName}
                   name="name"
                   id="inputName"
                   onChange={(e)=>setUserName(e.target.value)}/>
          </div>
          <div className="top">
            <label htmlFor="inputAge">Age</label>
            <input type="number"
                   value={userAge}
                   name="age"
                   id="inputAge"
                   onChange={(e)=>setUserAge(e.target.value)}/>
          </div>
          <div className="top">
            <label htmlFor="inputPhone">Phone</label>
            <input type="number"
                   value={userPhone}
                   name="phone"
                   id="inputPhone"
                   onChange={(e)=>setUserPhone(e.target.value)}/>
          </div>
          <div className="top">
            <label htmlFor="inputName">Email</label>
            <input type="text"
                   value={userEmail}
                   name="email"
                   id="inputName"
                   onChange={(e)=>setUserEmail(e.target.value)}/>
          </div>
          {
            (nameValid.valid === false)?<div className={"msg"}>{nameValid.msg}</div>:null
          }
        </div>

      <div className="btn-list">
        <div className="btn" onClick={() => {props.setCurrentStep(1)}}>zurück</div>
       <div className={"btn "+(nameValid.valid === true)?"valid":"invalid"}
           onClick={() => {
             if(nameValid.valid){
               props.setCurrentStep(3)
               props.setUserData({
               name:userName,
               age:userAge,
               email:userEmail,
               phone:userPhone
              })
             }
           }}>Bestellung abschließen</div>
      </div>
    </div>
  )
}

export default UserForm
