import React from 'react'
import {NavLink} from 'react-router-dom'
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';


function Navigation() {

    let navigate = useNavigate()
    const [value, setValue] = useState("") 
    const pages = [{
                   pageName: "Home",
                   to:"/"
                   },
                   {
                   pageName: "Page1",
                   to: "/page1"
                   },
                   {
                   pageName: "Page2",
                    to: "/page2"
}]



  return (
    <div>
        <input type="text" value={value} onChange={(e) => setValue(e.target.value)} ></input>
        <button onClick={() => navigate(`/${value} `)} >Click</button> 
        <nav>
            <ul className='navbar' >
                {
                    pages.map((page, index) => {
                        return (
                        <li key={index}>
                            <NavLink to={page.to} className="nav-bar-link" style={({isActive}) => {return {color: isActive ? 'green' : 'red'}}}  >{page.pageName}</NavLink>
                        </li>
                        )
                    })
                }
            </ul>
        </nav>
    </div>
  )
}

export default Navigation
