import React from "react";
import { useState } from "react";

class Info extends React.Component {



  constructor(props) {
    super(props);
    this.state = {
      name: 'Digital Natives',
      fach: 'Web Development'
    };
  }

  render() {
    return (
      <div>
        <h2>Wir sind die {this.state.name} und wir machen {this.state.fach}</h2>
      </div>
    );
  }
}

export default Info
